mapper.select:
select count(label_id) > 0
from tb_project_label
where repository_sid = #{repositorySid} and label_id = #{labelId}

select *
from tb_project_label
where (select label_ids from gh_issue where issue_number = #{issue_number}) like '%' || label_id::varchar||'%';

select *
from tb_project_label

    repository_sid = #{repositorySid}


        and (name ilike '%'||#{searchValue}||'%' or description ilike '%'||#{searchValue}||'%')


order by name;
